import './App.css';
import Form from './components/form';
import {useEffect, useState} from "react"
import ToDoList from './components/toDoList';

function App() {
  const [form, setForm] = useState()
  const [allTasks, setAllTasks] = useState()
  const [data, setData] = useState()

  useEffect(()=>{
    setData(allTasks)
  },[allTasks] )

  return (
    <div className="App">
      <Form  />
      <ToDoList tasks = {data}/>
    </div>
  );
}

export default App;
