import './App.css';
import Form from './components/form';
import {useEffect, useState} from "react"
import ToDoList from './components/toDoList';

function App() {
  const [form, setForm] = useState()
  const [allTasks, setAllTasks] = useState()
  const [data, setData] = useState([])
  const [dates, setDates] = useState([])
  // data is passed to to do list, all tasks is a fetch request when the form is updated 
  useEffect(()=>{
    console.log("hello")
    fetch("http://localhost:3001/task")
    .then(res => res.json())
    .then(res => {
      setData(res.task)
      res.task.map(task => {
        console.log(task)
        if (task.date !== "N/A"){
          setDates([...dates, task.date])
        }
      })
      console.log("date",dates)
    })
  },[] )

  function getByDate(event){
    console.log("getting date", event)
  }

  return (
    <div className="App">
      <header>
        <h1>The MIMB To Do List</h1>
      </header>
      <main>
        <Form  />
        <ToDoList tasks = {data}/>
        <select onChange={getByDate}>
          {dates.map((date)=>{
            <option value = 'date'>{date}</option>
          })}
        </select>
      </main>
    </div>
  );
}

export default App;
